DROP TABLE IF EXISTS `weapon`;
CREATE TABLE `weapon` (
  `item_id` MEDIUMINT UNSIGNED NOT NULL DEFAULT 0,
  `name` VARCHAR(120) NOT NULL DEFAULT '',
  `bodypart` VARCHAR(15) NOT NULL DEFAULT '',
  `crystallizable` VARCHAR(5) NOT NULL DEFAULT 'false',
  `weight` MEDIUMINT(5) NOT NULL DEFAULT 0,
  `soulshots` TINYINT(2) NOT NULL DEFAULT 0,
  `spiritshots` TINYINT(2) NOT NULL DEFAULT 0,
  `material` VARCHAR(15) NOT NULL DEFAULT 'wood',
  `crystal_type` VARCHAR(4) NOT NULL DEFAULT 'none',
  `p_dam` SMALLINT(3) NOT NULL DEFAULT 0,
  `rnd_dam` TINYINT(2) NOT NULL DEFAULT 0,
  `weaponType` VARCHAR(10) NOT NULL DEFAULT '',
  `critical` TINYINT(2) NOT NULL DEFAULT 0,
  `hit_modify` TINYINT(1) NOT NULL DEFAULT 0,
  `avoid_modify` TINYINT(1) NOT NULL DEFAULT 0,
  `shield_def` SMALLINT(3) NOT NULL DEFAULT 0,
  `shield_def_rate` TINYINT(2) NOT NULL DEFAULT 0,
  `atk_speed` SMALLINT(3) NOT NULL DEFAULT 0,
  `mp_consume` TINYINT(2) NOT NULL DEFAULT 0,
  `m_dam` SMALLINT(3) NOT NULL DEFAULT 0,
  `duration` SMALLINT(3) NOT NULL DEFAULT -1,           -- duration in minutes for shadow items
  `time` MEDIUMINT(6) NOT NULL DEFAULT -1,              -- duration in minutes for time limited items
  `price` INT UNSIGNED NOT NULL DEFAULT 0,
  `crystal_count` SMALLINT(4) UNSIGNED NOT NULL DEFAULT 0,
  `sellable` VARCHAR(5) NOT NULL DEFAULT 'false',
  `dropable` VARCHAR(5) NOT NULL DEFAULT 'false',
  `destroyable` VARCHAR(5) NOT NULL DEFAULT 'true',
  `tradeable` VARCHAR(5) NOT NULL DEFAULT 'false',
  `depositable` VARCHAR(5) NOT NULL DEFAULT 'false',
  `enchant4_skill` VARCHAR(70) NOT NULL DEFAULT '',
  `skills_onCast` VARCHAR(70) NOT NULL DEFAULT '',
  `skills_onCrit` VARCHAR(70) NOT NULL DEFAULT '',
  `change_weaponId` MEDIUMINT(5) NOT NULL DEFAULT 0,
  `skills_item` VARCHAR(70) NOT NULL DEFAULT '',
  PRIMARY KEY (`item_id`)
) DEFAULT CHARSET=utf8;






-- Fixes all improper weapon slotting.
UPDATE `weapon` SET bodypart = "rhand" WHERE (weaponType = "dagger") or (weaponType = "sword") or (weaponType = "rapier") or (weaponType = "crossbow");
UPDATE `weapon` SET bodypart = "lrhand" WHERE (weaponType = "pole") or (weaponType = "bow") or (weaponType = "ancient") or (weaponType = "bigsword") or (weaponType = "bigblunt") or (weaponType = "dualfist") or (weaponType = "dual") or (weaponType = "dualdagger");

UPDATE `weapon` SET time = "60" WHERE (name LIKE '%1 hour%') or (name LIKE '%1-hour%');
UPDATE `weapon` SET time = "180" WHERE (name LIKE '%3 hour%') or (name LIKE '%3-hour%');
UPDATE `weapon` SET time = "240" WHERE (name LIKE '%4 hour%') or (name LIKE '%4-hour%');
UPDATE `weapon` SET time = "1440" WHERE (name LIKE '%24 hour%') or (name LIKE '%24-hour%');
UPDATE `weapon` SET time = "4320" WHERE (name LIKE '%3 Day%') or (name LIKE '%3-Day%');
UPDATE `weapon` SET time = "10080" WHERE (name LIKE '%7 Day%') or (name LIKE '%7-Day%');
UPDATE `weapon` SET time = "21600" WHERE (name LIKE '%15 Day%') or (name LIKE '%15-Day%');
UPDATE `weapon` SET time = "43200" WHERE (name LIKE '%30 Day%') or (name LIKE '%30-Day%');
UPDATE `weapon` SET time = "86400" WHERE (name LIKE '%60 Day%') or (name LIKE '%60-Day%');

-- Ensures that all rapiers have the rapier skill, then sets the Infinity Rapier one to 0 (since it's called via the Infinity SA)
UPDATE `weapon` SET enchant4_skill = "3426-1" WHERE weaponType = "rapier";
UPDATE `weapon` SET enchant4_skill = "" WHERE item_id = "9388";

-- Weapons that are limited should not be able to be changed as far as I know. I do not know if changing a weapon will reset the timer on it. Until tested, this entry should remain. Just to be safe. ;)
UPDATE `weapon` SET change_weaponId = "0" WHERE (duration > 0) or (time > 0);

-- Despite this change not being EXACTLY retail-like, I find it maintains proper logic. It also prevents any possible exploit that might come from having retail information (prices and crystal counts) on NPC items.
UPDATE `weapon` SET crystallizable = "false", crystal_type = "none", price = "0", crystal_count = "0" WHERE name LIKE '%NPC Item %';

-- PvP weapons can not be changed via the Kamael skill, or traded, or dropped, or shipped, or sold. Just like augmented items but with more restrictions.
UPDATE `weapon` SET change_weaponId = "0", sellable = "false", dropable = "false", tradeable = "false" WHERE name LIKE '%{PvP}%';


